    greeting = "Good Morning";
    cssStyle.color = 'red';
} else if (curDate >= 12 && curDate < 19) {
    greeting = "Good Evening"; cssStyle.color = 'blue';
} else {
    greeting = "Good Night";
    cssStyle.color = 'green';
}

