import styled from "styled-components";

const FormControl = styled.div`
  margin: 0.5rem 0;
  & label {
    font-weight: bold;
    display: block;
    margin-bottom: 0.5rem;
    color: ${props ? "red" : "black"};
  }

  & input {
    display: block;
    width: 100%;
    border: 1px solid #ccc;
    font: inherit;
    padding: 0 0.25rem;
    line-height: 1.5rem;
  }
`;

export default FormControl;
